extractFeatures([pred(_,Sym,Pos,_)|L],[(pos:Pos)-W1,(Pos:Sym)-W2|F],S1,Len):- !, S2 is S1 + 5, extractFeatures(L,F,S2,Len), W1 is 1/Len, W2 is 2/Len.
extractFeatures([named(_,Sym,Sort,Type)|L],[(nam:Sym)-W1,(Sort:Type)-W2|F],S1,Len):- !, S2 is S1 + 5, extractFeatures(L,F,S2,Len), W1 is 1/Len, W2 is 2/Len.
extractFeatures([role(_,_,Role,_)|L],[(role:Role)-W|F],S1,Len):- !, S2 is S1 +1, extractFeatures(L,F,S2,Len), W is 1/Len.
extractFeatures([_|L],F,Sum,Len):- extractFeatures(L,F,Sum,Len).
